import React from 'react';
import { Bar } from 'react-chartjs-2';
import { stockData } from "./data";
import { useState } from 'react';

function check(fruits, location){

    for(var i = 0; i < fruits.length; i++)
    {
        if(fruits[i] == location)
        {
            return i;
        }
    }
    return -1;
  };

function check2(data, searchQuery){

    var fruits = [];
    for(var i = 0; i < data.length; i++)
    {
        var index = check(fruits, data[i].location);

        if(index === -1 && data[i].location.toLowerCase().includes(searchQuery))
        {
            fruits.push(data[i].location);
        }
    }
    return fruits;

  };

  function check3(data, country){

    var fruits = [];
    var len = []

    for(var i = 0; i < country.length; i++)
    {
        fruits.push(0);
        len.push(0);
    }

    for(var i = 0; i < country.length; i++)
    {
        for(var j = 0; j < data.length; j++)
        {
            if (country[i] === data[j].location)
            {
                fruits[i] += parseFloat(data[j].currSalary.replace("$", ""));
                len[i] += 1;
            }
        }
    }

    for(var i = 0; i < fruits.length; i++)
    {
        fruits[i] = fruits[i] / len[i];
    }
    return fruits;

  };

const options = {
  scales: {
    yAxes: [
      {
        ticks: {
          beginAtZero: true,
        },
      },
    ],
  },
};

export const VerticalBar = () => {
//   var fruits = funny(stockData);
  const { search } = window.location;
  const query = new URLSearchParams(search).get('s');
//   // var filteredFruits = filterPosts(fruits, query);
  const [searchQuery, setSearchQuery] = useState(query || '');
// const filteredFruits = filterPosts(fruits, searchQuery);

const data = {
  labels: check2(stockData, searchQuery),
  datasets: [
    {
      label: 'Mean Salary',
      data: check3(stockData, check2(stockData)),
      borderWidth: 0.5,
      backgroundColor: [
        'rgba(255, 99, 132, 0.2)',
        'rgba(54, 162, 235, 0.2)',
        'rgba(255, 206, 86, 0.2)',
        'rgba(75, 192, 192, 0.2)',
        'rgba(153, 102, 255, 0.2)',
      ],
    },
  ],
};

return (

  <>
  <form action="/" method="get" class="search">
            <h2>Interview Problem</h2>
              <label htmlFor="header-search">
                  <span className="visually-hidden">Search blog posts</span>
              </label>
              <input
                  // value={searchQuery}
                  // onInput={e => setSearchQuery(e.target.value)}
                  type="text"
                  id="header-search"
                  placeholder="Search blog posts"
                  name="s" 
              />
              <button type="submit">Search</button>
          </form>
    <div className='header'>
      <h1 className='title'>Vertical Bar Chart</h1>
      <div className='links'>
      </div>
    </div>
    <Bar data={data} options={options} />
  </>
);
}
