import React from "react";
import "./App.css";
import { stockData } from "./data";

const HomePageHeader = () => {
      return (
        // <header className="header">
        //   <h2>Your Stock Tracker</h2>
        // </header>
          <form action="/" method="get">
              <label htmlFor="header-search">
                  <span className="visually-hidden">Search blog posts</span>
              </label>
              <input
                  type="text"
                  id="header-search"
                  placeholder="Search blog posts"
                  name="s" 
              />
              <button type="submit">Search</button>
          </form>

      );
};



const Stock = ({ company, ticker}) => {
      if (!company) return <div />;
      return (
        <table>
          <tbody>
            <tr>
              <td>
                <h5>{company}</h5>
              </td>
              <td>
                <h5>{ticker}</h5>
              </td>
            </tr>
          </tbody>
        </table>
      );
    };

    function check(fruits, location){

        for(var i = 0; i < fruits.length; i++)
        {
            if(fruits[i].location == location)
            {
                return i;
            }
        }
        return -1;
      };

      const filterPosts = (posts, query) => {
        if (!query) {
            return posts;
        }

        return posts.filter((post) => {
            const postName = post.name.toLowerCase();
            return postName.includes(query);
        });
      };

    export const Stocks = () => {
        var fruits = [];
        var filteredFruits = [];

        const { search } = window.location;
        const query = new URLSearchParams(search).get('s');
        const filteredPosts = filterPosts(fruits, query);

          return (
        <>
              <HomePageHeader />
              <div className="stock-container">
                {stockData.map((data, key) => {
                    var index = check(fruits, data.location);
                    if(index === -1)
                    {
                        fruits.push({
                            location:   data.location,
                            currValue: parseFloat(data.currSalary.replace("$", "")),
                            noOf: 1
                        });
                    }
                    else{
                        fruits[index].currValue += parseFloat(data.currSalary.replace("$", ""));
                        fruits[index].noOf += 1;
                    }

                })}
                {
                filteredFruits.map((data, key) => {
                    var mean = data.currValue/data.noOf;
                  return (
                    <div key={key}>
                      <Stock
                        key={key}
                        company={data.location}
                        ticker={mean}
                      />
                    </div>
                  );
                })}
              </div>
            </>
          );
        };
