import React from "react";
import "./App.css";
import { stockData } from "./data";
import { useState } from 'react';

const HomePageHeader = ({ searchQuery, setSearchQuery }) => {
      return (
        // <header className="header">
        //   <h2>Your Stock Tracker</h2>
        // </header>
          <form action="/" method="get">
              <label htmlFor="header-search">
                  <span className="visually-hidden">Search blog posts</span>
              </label>
              <input
                  value={searchQuery}
                  onInput={e => setSearchQuery(e.target.value)}
                  type="text"
                  id="header-search"
                  placeholder="Search blog posts"
                  name="s" 
              />
              <button type="submit">Search</button>
          </form>

      );
};



const Stock = ({ company, ticker}) => {
      if (!company) return <div />;
      return (
        <table>
          <tbody>
            <tr>
              <td>
                <h5>{company}</h5>
              </td>
              <td>
                <h5>{ticker}</h5>
              </td>
            </tr>
          </tbody>
        </table>
      );
    };

    function check(fruits, location){

      if(fruits.length === 0)
      {
        return -1;
      }
        for(var i = 0; i < fruits.length; i++)
        {
            if(fruits[i].location === location)
            {
                return i;
            }
        }
        return -1;
      };

      function filterPosts(posts, query){

        var filteredFruits = []
        if (!query) {
            return posts;
        }

        for(var i = 0; i < posts.length; i++)
        {
          if(posts[i].location.toLowerCase().includes(query))
          {
            filteredFruits.push(posts[i]);
          }
        }

        return filteredFruits;
      };

      function funny(data){

        var fruits = []; 
        for(var i = 0; i < data.length; i++)
        {
          var index = check(fruits, data[i].location);
          if(index === -1)
          {
              fruits.push({
                  location:   data[i].location,
                  currValue: parseFloat(data[i].currSalary.replace("$", "")),
                  noOf: 1
              });
          }
          else{
              fruits[index].currValue += parseFloat(data[i].currSalary.replace("$", ""));
              fruits[index].noOf += 1;
          }
        }
        return fruits;
      }

    export const Stocks = () => {
        var fruits = funny(stockData);
        const { search } = window.location;
        const query = new URLSearchParams(search).get('s');
        // var filteredFruits = filterPosts(fruits, query);
        const [searchQuery, setSearchQuery] = useState(query || '');
    const filteredFruits = filterPosts(fruits, searchQuery);

          return (
        <>
              <HomePageHeader 
                searchQuery={searchQuery}
                setSearchQuery={setSearchQuery}
              />
              <div className="stock-container">
                {
                filteredFruits.map((data, key) => {
                    var mean = data.currValue/data.noOf;
                  return (
                    <div key={key}>
                      <Stock
                        key={key}
                        company={data.location}
                        ticker={mean}
                      />
                    </div>
                  );
                })}
              </div>
            </>
          );
        };
