import React from "react";
import "./App.css";
import { stockData } from "./data";

const HomePageHeader = () => {
      return (
        <header className="header">
          <h2>Your Stock Tracker</h2>
        </header>
      );
};

const Stock = ({ company, ticker}) => {
      if (!company) return <div />;
      return (
        <table>
          <tbody>
            <tr>
              <td>
                <h5>{company}</h5>
              </td>
              <td>
                <h5>{ticker}</h5>
              </td>
            </tr>
          </tbody>
        </table>
      );
    };

    const check = ({ fruits, location}) => {

        for(var i = 0; i < fruits.length; i++)
        {
            if(fruits[i].location == location)
            {
                return false;
            }
        }
        return true;
      };

    export const Stocks = () => {
        var fruits = [];
          return (
        <>
              <HomePageHeader />
              <div className="stock-container">
                {stockData.map((data, key) => {
                    if(check(fruits, data.location))
                    {
                        fruits.push({
                            location:   data.location,
                            currValue: data.currSalary
                        });
                    }
                    else{
                        fruits[key].currValue += data.currSalary
                    }

                })}
                {fruits.map((data, key) => {
                  return (
                    <div key={key}>
                      <Stock
                        key={key}
                        company={data.location}
                        ticker={data.currValue}
                      />
                    </div>
                  );
                })}
              </div>
            </>
          );
        };
