import React from "react";
import "./App.css";
import { stockData } from "./data";

const HomePageHeader = () => {
      return (
        <Navbar bg="light" expand="lg">
  <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
  <Navbar.Toggle aria-controls="basic-navbar-nav" />
  <Navbar.Collapse id="basic-navbar-nav">
    <Nav className="mr-auto">
      <Nav.Link href="#home">Home</Nav.Link>
      <Nav.Link href="#link">Link</Nav.Link>
      <NavDropdown title="Dropdown" id="basic-nav-dropdown">
        <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
        <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
        <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
        <NavDropdown.Divider />
        <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
      </NavDropdown>
    </Nav>
    <Form inline>
      <FormControl type="text" placeholder="Search" className="mr-sm-2" />
      <Button variant="outline-success">Search</Button>
    </Form>
  </Navbar.Collapse>
</Navbar>
      );
};

const Stock = ({ company, ticker}) => {
      if (!company) return <div />;
      return (
        <table>
          <tbody>
            <tr>
              <td>
                <h5>{company}</h5>
              </td>
              <td>
                <h5>{ticker}</h5>
              </td>
            </tr>
          </tbody>
        </table>
      );
    };

    function check(fruits, location){

        for(var i = 0; i < fruits.length; i++)
        {
            if(fruits[i].location == location)
            {
                return i;
            }
        }
        return -1;
      };


    export const Stocks = () => {
        var fruits = [];
          return (
        <>
              <HomePageHeader />
              <div className="stock-container">
                {stockData.map((data, key) => {
                    var index = check(fruits, data.location);
                    if(index === -1)
                    {
                        fruits.push({
                            location:   data.location,
                            currValue: parseFloat(data.currSalary.replace("$", "")),
                            noOf: 1
                        });
                    }
                    else{
                        fruits[index].currValue += parseFloat(data.currSalary.replace("$", ""));
                        fruits[index].noOf += 1;
                    }

                })}
                {fruits.map((data, key) => {
                    var mean = data.currValue/data.noOf;
                  return (
                    <div key={key}>
                      <Stock
                        key={key}
                        company={data.location}
                        ticker={mean}
                      />
                    </div>
                  );
                })}
              </div>
            </>
          );
        };
